mapper.select:
SELECT s.*,c.name AS clazz_name
FROM student AS s LEFT JOIN clazz c
ON s.clazz_id=c.id


        and s.name like concat('%',#{name},'%')



        and s.degree=#{degree}



        and s.clazz_id=#{clazzId}

mapper.update:
update student
name=#{name}, no=#{no}, phone=#{phone}, gender=#{gender}, degree=#{degree}, id_card=#{idCard}, is_college=#{isCollege}, address=#{address}, graduation_date=#{graduationDate}, violation_count=#{violationCount}, violation_score=#{violationScore}, clazz_id=#{clazzId}, update_time=#{updateTime}
where id=#{id}
