mapper.select:
SELECT c.*,e.name AS master_name, (CASE WHEN CURDATE() > end_date THEN '已结课' WHEN CURDATE() < begin_date THEN '未开课' ELSE '在读中' END) AS status FROM clazz c LEFT JOIN emp e ON c.master_id=e.id
and c.name LIKE CONCAT('%',#{name},'%')
and c.end_date >= #{begin}
and c.end_date <= #{end}

mapper.update:
update clazz
name=#{name}, room=#{room}, begin_date=#{beginDate}, end_date=#{endDate}, master_id=#{masterId}, subject=#{subject}, update_time=#{updateTime},
where id=#{id}
